td.script:



// Draw a line chart
var svg = d3.select('svg'),
  margin = { top:7, right:100, bottom:80, left:120 },
  width = +svg.attr('width') - margin.left - margin.right,
  height = +svg.attr('height') - margin.top - margin.bottom,
  g = svg.append('g').attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

var div = d3.select(".svg-wrapper").append("div").attr("class", "toolTip");

// Set the X scale
var x = d3.scale.linear().range([0, width],5);

// Set the Y scale
var y = d3.scale.linear().range([height, 0],5);

// Set the color scale
var color = d3.scale.category10();

var xAxis = d3.svg.axis()
.scale(x)

var yAxis = d3.svg.axis()
.scale(y)
.orient("left");

var line = d3.svg.line()
.x(function(d) {
  return x(d.date);
})
.y(function(d) {
  return y(d.attackType);
});

  // load the data
  var data = JSON.parse((document.getElementById("progress").innerHTML).replace(/'/g, '"'))

  // Select the important columns
  color.domain(d3.keys(data[0]).filter(function(key) {
      return key !== "labels";
  }));
  // Correct the types
  data.forEach(function(d) {
    d.date =(d.labels);
  });

  var attackTypes = color.domain().map(function(name) {
    return {
      name: name,
      values: data.map(function(d) {
        return {
          date: d.date,
          attackType: +d[name]
        };
      })
    };
  });

  // Set the X domain
  x.domain(d3.extent(data, function(d) {
    return d.date;
  }));


  // Set the Y domain
  y.domain([
    d3.min(attackTypes, function(c) {
      return d3.min(c.values, function(v) {
        return v.attackType;
      });
    }),
    d3.max(attackTypes, function(c) {
      return d3.max(c.values, function(v) {
        return v.attackType;
      });
    })
  ]);

  // Set the X axis
  g.append("g")
    .attr("class", "x axis")
    .attr("transform", "translate(0," + height + ")")
    .call(xAxis);

  // Set the Y axis
  g.append("g")
    .attr("class", "y axis")
    .call(yAxis)


 // text label for the x  and y axis
g.append("text")             
    .attr("transform",
          "translate(" + (width/2) + " ," + 
                         (height + margin.top + 20) + ")")
    .style("text-anchor", "middle")
    .attr("dy", "2em")
    .text(data[3].xaxis);

 g.append("text")
    .attr("transform", "rotate(-90)")
    .attr("y", 0 - margin.left)
    .attr("x",0 - (height / 2))
    .attr("dy", "1em")
    .style("text-anchor", "middle")
    .text("DRY DENSITY (g/cc)");  

  // Draw the lines
  var attacker = g.selectAll(".attacker")
    .data(attackTypes)
    .enter().append("g")
    .attr("class", "attacker");

  attacker.append("path")
    .attr("class", "line")
    .attr("d", function(d) {
      return line(d.values);
    })
    .style("stroke", function(d) {
      return color(d.name);
    })

  // Add the circles
  attacker.append("g").selectAll("circle")
    .data(function(d){return d.values})
    .enter()
    .append("circle")
    .attr("r", 3)
    .attr("cx", function(dd){return x(dd.date)})
    .attr("cy", function(dd){return y(dd.attackType)})
    .attr("fill", "none")
    .attr("stroke", function(d){return color(this.parentNode.__data__.name)});




